import React, { useState, useRef } from 'react';
import axios from 'axios';

const App = () => {
  const [isTable, setIsTable] = useState(false);  // Stan kontrolujący widok (formularz/tabela)
  const [formData, setFormData] = useState({
    name: '',
    url: '',
    text: ''
  });
  const [tableData, setTableData] = useState([]);

  const formRef = useRef(null);  // Ref dla formularza
  const tableRef = useRef(null);  // Ref dla tabeli

  // Obsługuje zmiany w formularzu
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Wysyła dane formularza do serwera i dodaje do tabeli
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('your-api-endpoint', formData);
      setTableData([...tableData, response.data]);  // Dodaje dane do tabeli
      setFormData({ name: '', url: '', text: '' });  // Resetuje formularz
    } catch (error) {
      console.error("Error sending data", error);
    }
  };

  // Przełącza widok między formularzem a tabelą
  const toggleView = () => {
    setIsTable(!isTable);
  };

  return (
    <div className="container mt-4">
      <button className="btn btn-primary mb-3" onClick={toggleView}>
        {isTable ? 'Form' : 'Table'}
      </button>

      {/* Wyświetlanie formularza lub tabeli na podstawie stanu */}
      {isTable ? (
        <div ref={tableRef}>
          <table className="table table-striped table-bordered">
            <thead>
              <tr>
                <th>Name</th>
                <th>URL</th>
                <th>Text</th>
              </tr>
            </thead>
            <tbody>
              {tableData.map((data, index) => (
                <tr key={index}>
                  <td>{data.name}</td>
                  <td>{data.url}</td>
                  <td>{data.text}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div ref={formRef}>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label" htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                className="form-control"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label" htmlFor="url">URL:</label>
              <input
                type="text"
                id="url"
                name="url"
                className="form-control"
                value={formData.url}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label" htmlFor="text">Text:</label>
              <textarea
                id="text"
                name="text"
                className="form-control"
                value={formData.text}
                onChange={handleChange}
                required
              />
            </div>
            <button type="submit" className="btn btn-success">Submit</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default App;
