import { useState, useRef } from "react";
import axios from "axios";

function Form()
{
    const name = useRef();
    const url = useRef();
    const text = useRef();
    const [data, setData] = useState([]); // Stan przechowujący wiersze tabeli

    const handleSubmit = async(e) => {
        e.preventDefault();

        const formData = {
            name: name.current.value,
            url: url.current.value,
            text: text.current.value
        };

        try {
            const response = await axios.post("http://localhost:8000/data", formData);
            console.log("Odpowiedź serwera:", response.data);

            // Dodaj nowy wiersz do tabeli
            setData(prevData => [...prevData, formData]);

            // Wyczyszczenie pól formularza
            nameRef.current.value = "";
            urlRef.current.value = "";
            textRef.current.value = "";
        } catch (error) {
            console.error("Błąd wysyłania danych:", error);
        }

    }
    return(
        <>
            <div>
                <form onSubmit={handleSubmit}>
                    <label>Name: </label><input type="text" ref={name}/><br></br>
                    <label>Url: </label><input type="text" ref={url}/><br></br>
                    <label>Text: </label><input type="text" ref={text}/><br></br>
                    <input type="submit"/>
                </form>
            </div>
        </>
    )
}

export default Form;
